  if (user.netid === updatedUserAttributes.netid) {
    // Merge the updated attributes into the user object
    const updatedUser = { ...user, ...updatedUserAttributes };
    console.log('updatedUser:', updatedUser);

    // Update the user object
    user = updatedUser;

    // You can also save the updated user object back to the file
