  console.error('Error reading user data:', err);
  // Handle the error, e.g., create a new user object
  user = {
    netid: "jones1",
    password: "1234",
    // Other user attributes...
  };
}


// Function to update user attributes
export function updateUser(updatedUserAttributes) {
  // Check if the user exists based on userId (netid)
  if (user.netid === updatedUserAttributes.netid) {
    // Merge the updated attributes into the user object
    const updatedUser = { ...user, ...updatedUserAttributes };
    console.log('updatedUser:', updatedUser);

    // Update the user object
    user = updatedUser;

    // You can also save the updated user object back to the file
    // try {
    //   fs.writeFileSync('CurrentUser.json', JSON.stringify(updatedUser, null, 2), 'utf8');
    // } catch (err) {
    //   console.error('Error writing user data to file:', err);
    // }

    return updatedUser;
  } else {
    // Handle the case where the user does not exist
    return null;
  }
}

// Function to retrieve user data by netid
export function getUser(input_user) {
  return user;
}
