import users from './users'; // Import your user data

export function getUsersWithCompatibleAvailability(currentUser) {
  const compatibleUsers = users.filter((user) => {
    // TODO: 
    // Implement the logic to check compatibility
    // Return true if the user is compatible with the currentUser
    return isCompatible(user, currentUser);
  });
  return compatibleUsers;
}

function isCompatible(user, currentUser) {
    // Check if both users have availability for lunchtime
    if (user.availability && currentUser.availability) {
      // Get the lunchtime availability for both users
      const userLunchAvailability = user.availability;
      const currentUserLunchAvailability = currentUser.availability;

      // Check if there is an overlapping time window
      for (const userSlot of userLunchAvailability) {
        for (const currentSlot of currentUserLunchAvailability) {
          const userStart = new Date(`1970-01-01 ${userSlot.start}`);
          const userEnd = new Date(`1970-01-01 ${userSlot.end}`);
          const currentUserStart = new Date(`1970-01-01 ${currentSlot.start}`);
          const currentUserEnd = new Date(`1970-01-01 ${currentSlot.end}`);

          // Check if there is an overlap in time
          if (userStart <= currentUserEnd && userEnd >= currentUserStart) {
            return true; // Users are compatible
          }
        }
      }
    }

    return false; // Users are not compatible for lunch
  }
