import { db, ref, get, update } from './DBUtils'; // Import necessary functions
import { getFirestore, collection, query, where, getDocs } from 'firebase/firestore';

// Function to retrieve user data by netId
export async function getUser(netId) {
  try {
    console.log(netId);

    // Create a reference to the "users" collection
    const usersCollection = collection(db, 'users');

    // Create a query to find the user with the specified netId
    const q = query(usersCollection, where('netId', '==', netId));

    // Execute the query and get the result
    const querySnapshot = await getDocs(q);

    if (!querySnapshot.empty) {
      // Assuming there is only one user with the specified netId
      const userData = querySnapshot.docs[0].data();
      console.log(userData);
      return userData;
    } else {
      // Handle the case where the user does not exist
      return null;
    }
  } catch (error) {
    console.error('Error fetching user data:', error);
    return null;
  }
}

// Function to update user attributes
export async function updateUser(updatedUserAttributes) {
  const userId = updatedUserAttributes.netid; // Assuming 'netid' is the user's identifier

  try {
    // Create a reference to the user's data in the Firebase database
    const userRef = ref(db, 'users/' + userId);

    // Fetch the user's data
    const userSnapshot = await get(userRef);

    if (userSnapshot.exists()) {
      const user = userSnapshot.val();
      const updatedUser = { ...user, ...updatedUserAttributes };

      // Update the user's data in the database
      await update(userRef, updatedUser);

      return updatedUser;
    } else {
      // Handle the case where the user does not exist
      return null;
    }
  } catch (error) {
    console.error('Error updating user data:', error);
    return null;
  }
}

