// EditProfile.js
import React, { useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import { getUser, updateUser } from './utilities';

export const Profilecontainer= () => {
  const { user } = useParams();
  const initialUser = getUser(user);

  // State to capture changes in user attributes
  const [editedUser, setEditedUser] = useState(initialUser);

  const handleSave = () => {
    // Call a function to update the user data with the changes
    updateUser(user, editedUser);
  };

  return (
    <div>
      <h2>Edit Profile</h2>
      {/* Input fields for editing user attributes */}
      <label>
        First Name:
        <input
          type="text"
          value={editedUser.first_name}
          onChange={(e) => setEditedUser({ ...editedUser, first_name: e.target.value })}
        />
      </label>
      {/* Add more input fields for other user attributes here */}
      <button onClick={handleSave}>Save</button>
      <Link to={`/profile/${user}`}>Back to Profile</Link>
    </div>
  );
};
