import React, { useState, useEffect } from 'react';
import UserCard from './UserCard';
import { getUsersWithCompatibleAvailability } from './AvailibilityUtils';
import {getUser} from './CurrentUser';
import { Link, useParams } from 'react-router-dom';

export const Searchcontainer = () => {
  const { user_params } = useParams();
  const user = getUser(user_params);

  console.log('User Data:', user.availability);


  const [compatibleUsers, setCompatibleUsers] = useState([]);

  // TODO:
  // let user select a day to check for availibility for that day
  // basically you have fucniotn to check what day is the date
  // then add one more argument 'day' in the function 
  // eg. getUsersWithCompatibleAvailability(user, day);
  // you also have to modify the getUsersWithCompatibleAvailability in userUtils.js
  // and change the logic in isComapitble(), etc.

  // TODO:
  // location and time filtering


  useEffect(() => {
    const users = getUsersWithCompatibleAvailability(user);
    setCompatibleUsers(users);
  }, [user]);

  return (
    <div>
      <h1>Search for Lunchtime Companions</h1>
      {compatibleUsers.length > 0 ? (
        compatibleUsers.map((user) => <UserCard key={user.id} user={user} />)
      ) : (
        <p>No compatible users found.</p>
      )}
    </div>
  );
};
